import { Component, OnInit } from "@angular/core";

@Component({
  selector: "app-form",
  templateUrl: "./form.component.html",
  styleUrls: ["./form.component.css"],
})
export class FormComponent implements OnInit {
  value = "";
  invalid = false;
  message = "";

  constructor() {}

  ngOnInit(): void {
    this.message = "";
  }

  onSubmit() {
    if (this.value == undefined) {
      this.invalid = true;
      this.message = "O campo título da tarefa não pode ficar vazio";
      return;
    }

    this.invalid = false;
    this.message = "Tarefa cadastrada com sucesso";
  }
}
<div
  id="message"
  class="card-panel white-text"
  [hidden]="!this.message"
  [classRed]="this.invalid"
  [classBlue]="!this.invalid"
>
  {{ this.message }}
</div>
<div class="row">
  <form class="col s12" #form="ngForm" (submit)="onSubmit()">
    <div class="row">
      <div class="input-field col s12">
        <input
          id="task"
          type="text"
          class="validate"
          [(ngModel)]="this.value"
        />
        <label for="task">Título da tarefa</label>
      </div>
    </div>
    <div class="row">
      <div class="input-field col s12">
        <input id="date" type="text" class="validate" />
        <label for="date">Data</label>
      </div>
    </div>
    <div class="row">
      <button class="waves-effect waves-light btn orange">salvar</button>
    </div>
  </form>
</div>
