import { Component, OnInit, ViewChild } from "@angular/core";
import { NgForm } from "@angular/forms";

import { TaskService } from "./../services/task.service";
import { Task } from "./../model/task";

@Component({
  selector: "app-form",
  templateUrl: "./form.component.html",
  styleUrls: ["./form.component.css"],
})
export class FormComponent implements OnInit {
  @ViewChild("form") form!: NgForm;

  task!: Task;
  tasks?: Task[];

  isSubmitted!: boolean;
  isShowMessage: boolean = false;
  isSuccess!: boolean;
  message!: string;

  constructor(private taskService: TaskService) {}

  ngOnInit(): void {
    this.task = new Task("");
    this.tasks = this.taskService.getTasks();
  }

  onSubmit() {
    this.isSubmitted = true;
    if (!this.taskService.isExist(this.task.title)) {
      this.taskService.save(this.task);
    } else {
      this.taskService.update(this.task);
    }
    this.isShowMessage = true;
    this.isSuccess = true;
    this.message = "Cadastro realizado com sucesso!";

    this.form.reset();
    this.task = new Task("");

    this.tasks = this.taskService.getTasks();
  }
}
<div class="row">
  <div class="col s12">
    <div
      id="msg-login"
      class="card-panel white-text"
      [class.red]="!this.isSuccess"
      [class.blue]="this.isSuccess"
      *ngIf="this.isShowMessage"
    >
      {{ this.message }}
    </div>
  </div>
  <div class="row">
    <form class="col s12" #form="ngForm" (submit)="onSubmit()">
      <div class="row">
        <div class="input-field col s12">
          <input
            id="input-title"
            name="input-title"
            type="text"
            class="validate"
            [(ngModel)]="this.task.title"
            pattern="^(\s?[A-ZÀ-Ú][a-zà-ú]*)+(\s[a-zà-ú]*)?(\s[A-ZÀ-Ú][a-zà-ú]*)+"
            #title="ngModel"
            required
          />
          <label for="input-title">Título da tarefa *</label>
          <div class="red-text" [hidden]="title.valid || title.untouched">
            Por favor, informe o título da tarefa.
          </div>
        </div>
      </div>

      <div class="row">
        <div class="input-field col s12 l6">
          <input
            id="input-date"
            name="input-date"
            type="date"
            class="validate"
            [(ngModel)]="this.task.date"
          />
          <label for="input-date">Data</label>
        </div>
      </div>

      <div class="row">
        <div class="col s12 l6">
          <button
            class="waves-effect waves-light btn orange"
            type="submit"
            [disabled]="!form.valid"
          >
            salvar
          </button>
        </div>
      </div>
    </form>
  </div>
</div>
