import { Component, OnInit } from "@angular/core";
import { ActivatedRoute } from "@angular/router";

import { TaskService } from "src/app/services/task.service";
import { Task } from "src/app/model/task";

@Component({
  selector: "app-edit",
  templateUrl: "./edit.component.html",
  styleUrls: ["./edit.component.css"],
})
export class EditComponent implements OnInit {
  task: Task;
  isSaving: boolean = false;

  constructor(public taskService: TaskService, private route: ActivatedRoute) {
    this.task = {
      id: this.route.snapshot.params["id"],
      title: "",
      status: false,
      date: new Date(),
    };
  }

  ngOnInit(): void {
    this.projectService
      .show(this.route.snapshot.params["id"])
      .then(({ data }) => {
        this.project = data;
      })
      .catch((error) => {
        return error;
      });
  }

  handleSave() {
    this.isSaving = true;
    this.projectService
      .update(this.project)
      .then(({ data }) => {
        this.isSaving = false;
        Swal.fire({
          icon: "success",
          title: "Project saved successfully!",
          showConfirmButton: false,
          timer: 1500,
        });
        return data;
      })
      .catch((error) => {
        this.isSaving = false;
        Swal.fire({
          icon: "error",
          title: "An Error Occured!",
          showConfirmButton: false,
          timer: 1500,
        });
        return error;
      });
  }
}
<div class="row">
  <div class="col s12">
    <div
      id="msg-login"
      class="card-panel white-text"
      [class.red]="!this.isSuccess"
      [class.blue]="this.isSuccess"
      *ngIf="this.isShowMessage"
    >
      {{ this.message }}
    </div>
  </div>
  <div class="row" *ngIf="task">
    <form class="col s12" #form="ngForm" (submit)="onSubmit()">
      <div class="row">
        <div class="input-field col s12">
          <input
            id="input-title"
            name="input-title"
            type="text"
            class="validate"
            [(ngModel)]="this.task.title"
            pattern="^([A-ZÀ-Ú][a-zà-ú]*)+(\s[a-zà-ú]*)+"
            #title="ngModel"
            required
          />
          <label for="input-title">Título da tarefa *</label>
          <div class="red-text" [hidden]="title.valid || title.untouched">
            Por favor, informe o título da tarefa.
          </div>
        </div>
      </div>

      <div class="row">
        <div class="input-field col s12 l6">
          <input
            id="input-date"
            name="input-date"
            type="date"
            class="validate"
            [(ngModel)]="this.task.date"
          />
          <label for="input-date">Data</label>
        </div>
      </div>

      <div class="row">
        <div class="col s12 l6">
          <button
            class="waves-effect waves-light btn orange"
            type="submit"
            [disabled]="!form.valid"
          >
            salvar
          </button>
        </div>
      </div>
    </form>
  </div>
</div>
