import { Component, OnInit } from "@angular/core";
import { Router } from "@angular/router";

import { Shared } from "./../util/shared";
import { TaskService } from "./../services/task.service";
import { Task } from "./../model/task";
import { WebStorageUtil } from "./../util/web-storage-util";
import { Constants } from "./../util/constants";

@Component({
  selector: "app-table",
  templateUrl: "./table.component.html",
  styleUrls: ["./table.component.css"],
})
export class TableComponent implements OnInit {
  URL = "http://localhost:3000/tasks";

  task!: Task;
  tasks?: Task[];

  isSubmitted!: boolean;
  isShowMessage: boolean = false;
  isSuccess!: boolean;
  message!: string;

  constructor(private taskService: TaskService, private router: Router) {}

  ngOnInit(): void {
    this.taskService.getTasks().subscribe((items) => {
      this.tasks = items;
    });
  }

  /**
   * Realiza o clone do objeto, justamente para não refletir as mudanças
   * imediatamente na lista de tarefas cadastrados sem pressionar o submit.
   * @param task
   */
  onEdit(task: Task) {
    this.task = task;
    this.router.navigate(["/tarefa/", this.task.id]);
  }
  /*
  onDelete(title: string) {
    let confirmation = window.confirm(
      "Você tem certeza que deseja remover " + title
    );
    if (!confirmation) {
      return;
    }
    let response: boolean = this.taskService.delete(title);
    this.isShowMessage = true;
    this.isSuccess = response;
    if (response) {
      this.message = "O item foi removido com sucesso!";
    } else {
      this.message = "Opps! O item não pode ser removido!";
    }
    this.taskService.getTasks().subscribe((items) => {
      this.tasks = items;
    });
  }
  */
}
<table class="striped" *ngIf="tasks && tasks!.length > 0">
  <tbody>
    <tr *ngFor="let t of tasks">
      <td class="title-task">
        <label for="{{ t.title }}">
          <input
            *ngIf="t.status"
            type="checkbox"
            id="{{ t.title }}"
            class="filled-in"
            checked="checked"
          />
          <input
            *ngIf="!t.status"
            type="checkbox"
            id="{{ t.title }}"
            class="filled-in"
          />
          <span>{{ t.title }}</span>
        </label>
      </td>
      <td>{{ t.date | date : "dd/MM/yyyy" }}</td>
      <td class="action">
        <a class="waves-effect waves-light btn orange" (click)="onEdit(t)"
          ><i class="material-icons left">edit</i>editar</a
        >
        <a
          class="waves-effect waves-light btn orange btn-excluir"
          (click)="onDelete(t.id)"
          ><i class="material-icons left">delete</i>EXCLUIR</a
        >
      </td>
    </tr>
  </tbody>
</table>

<div *ngIf="!tasks || tasks!.length == 0">
  <div class="row">
    <div class="center-align">
      <i class="col s12 material-icons large">mood_bad</i>
      <span class="col s12 flow-text"
        >Ainda não temos tarefas cadastradas!</span
      >
    </div>
  </div>
</div>
