import { Component, OnInit } from "@angular/core";

import { TaskService } from "./../services/task.service";
import { Task } from "./../model/task";

@Component({
  selector: "app-table",
  templateUrl: "./table.component.html",
  styleUrls: ["./table.component.css"],
})
export class TableComponent implements OnInit {
  task!: Task;
  tasks?: Task[];

  isShowMessage: boolean = false;
  isSuccess!: boolean;
  message!: string;

  constructor(private taskService: TaskService) {}

  ngOnInit(): void {
    this.retrieveTask();
  }

  retrieveTask(): void {
    this.taskService.getTasks().subscribe({
      next: (data) => {
        this.tasks = data;
      },
      error: (e) => console.error(e),
    });
  }

  onUpdateStatus(id: number) {
    this.taskService.getById(id).subscribe({
      next: (data) => {
        this.task = data;
      },
      error: (e) => console.error(e),
    });
    const data = {
      id: this.task.id,
      title: this.task.title,
      status: this.task.status === true ? false : true,
      date: this.task.date,
    };

    this.taskService.update(data).subscribe({
      next: (res) => {
        this.isShowMessage = true;
        this.isSuccess = true;
        this.message = "Tarefa realizada!";
      },
      error: () => {
        this.isShowMessage = true;
        this.isSuccess = false;
        this.message = "Erro ao marcar tarefa como realizada.";
      },
    });
  }

  onDelete(id: number) {
    this.taskService.delete(id).subscribe({
      next: (res) => {
        this.isShowMessage = true;
        this.isSuccess = true;
        this.message = "Tarefa excluída com sucesso!";
        this.retrieveTask();
      },
      error: () => {
        this.isShowMessage = true;
        this.isSuccess = false;
        this.message = "Erro ao excluir a tarefa.";
      },
    });
  }
}
<div class="col s12">
  <div
    id="msg-login"
    class="card-panel white-text"
    [class.red]="!this.isSuccess"
    [class.blue]="this.isSuccess"
    *ngIf="this.isShowMessage"
  >
    {{ this.message }}
  </div>
</div>
<table class="striped" *ngIf="tasks && tasks!.length > 0">
  <tbody>
    <tr *ngFor="let t of tasks">
      <td class="title-task">
        <label for="{{ t.title }}">
          <input
            *ngIf="t.status"
            type="checkbox"
            id="{{ t.title }}"
            class="filled-in"
            checked="checked"
            (click)="onUpdateStatus(t.id!)"
          />
          <input
            *ngIf="!t.status"
            type="checkbox"
            id="{{ t.title }}"
            class="filled-in"
            (click)="onUpdateStatus(t.id!)"
          />
          <span>{{ t.title }}</span>
        </label>
      </td>
      <td>{{ t.date | date : "dd/MM/yyyy" }}</td>
      <td class="action">
        <a
          [routerLink]="['/tarefa', t.id, 'edit']"
          class="waves-effect waves-light btn orange"
          ><i class="material-icons left">edit</i>editar</a
        >
        <a
          class="waves-effect waves-light btn orange btn-excluir"
          (click)="onDelete(t.id!)"
          ><i class="material-icons left">delete</i>EXCLUIR</a
        >
      </td>
    </tr>
  </tbody>
</table>

<div *ngIf="!tasks || tasks!.length == 0">
  <div class="row">
    <div class="center-align">
      <i class="col s12 material-icons large">mood_bad</i>
      <span class="col s12 flow-text"
        >Ainda não temos tarefas cadastradas!</span
      >
    </div>
  </div>
</div>
