import { Component, OnInit } from "@angular/core";
import { Router } from "@angular/router";

@Component({
  selector: "app-header",
  templateUrl: "./header.component.html",
  styleUrls: ["./header.component.css"],
})
export class HeaderComponent implements OnInit {
  currentUrl: string;

  constructor(private router: Router) {}

  ngOnInit(): void {
    console.log(this.router.url); // Exibe a URL atual no console
    this.currentUrl = this.router.url; // Atribui a URL atual a uma variável
  }
}
<nav class="light-blue lighten-1">
  <div class="nav-wrapper container">
    <a routerLink="/" id="logo-container" class="brand-logo title"
      >MINHAS TAREFAS</a
    >
    <ul class="right" *ngIf="routerLink != '/'">
      <li>
        <a
          routerLink="/tarefa/create"
          id="download-button"
          class="btn-large waves-effect waves-light orange"
          >NOVA TAREFA</a
        >
      </li>
    </ul>
  </div>
</nav>
